import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

import Navigation from './components/Navigation'; //<-- <Navigation />
import TodoForm   from './components/TodoForm'; //<-- <TodoForm />
import { todos }  from './todos.json'; //<-- #
import Swal from 'sweetalert2'


//import { saludar, despedir, numero } from './helpers/Hello.js'; //<-- Importacion normal
//import { * } from './helpers/Hello.js'; //<-- Importar todos... se ejecuta: Helper.saludar('Ryu')
import { saludar as S, despedir as D, numero as N } from './helpers/Hello.js'; //<-- Permite ponerle un alias a cada funcion del modulo importado

import calcular from './helpers/AreaCirculo.js'; //<-- Permite ponerle un nombre propio al importar


//** ▼ Ejecucion de los modulos importados ▼ **/
	//saludar('Luigi')
	S('Sonic')

	//despedir('Wario')
	D('Tails')

	//console.log( numero )
	console.log( N )

	console.log( calcular(56) )
//** ▲ Ejecucion de los modulos importados ▲ **/

class App extends Component {

	constructor(){
		super()
		this.state = { todos } //<-- { todos : todos } //<-- #
		this.handleAddToDO = this.handleAddToDO.bind(this)
	}

	handleAddToDO(todo){
		this.setState({
			todos : [...this.state.todos, todo] //<-- Desarmar el array de Objects y rearmarlo con el nuevo Object ( evitando usar un .push() )
		})
	}
	handleDeleteTodo(index){

		Swal.fire({
			title: 'Are you sure?',
			text: "You won't be able to revert this!",
			type: 'warning',
			showCancelButton: true,
			confirmButtonColor: '#3085d6',
			cancelButtonColor: '#d33',
			confirmButtonText: 'Yes, delete it!'
		}).then((result) => {
			if (result.value) {

				this.setState({
					// todos : this.state.todos.filter( (state, i) => {
					// 	return i !== index
					// }) //or...

					todos : this.state.todos.filter( (state, i) => i !== index )
				})

				Swal.fire(
					'Deleted!',
					'Your file has been deleted.',
					'success'
				)
			}
		})


	}

	render(){

		const itemsToDOs = this.state.todos.map( (todo, theIndex) => {
			return (
				<div className="col-md-3" key={theIndex}>
					<div className="card mt-4">
						<div className="card-header">
							<h3>{todo.title}</h3>
							<span className="badge badge-pill btn-danger">
								{todo.priority}
							</span>
						</div>
						<div className="card-body">
							<p>{todo.description}</p>
							<p>{todo.responsible}</p>
						</div>
						<div className="card-footer">
							<button
								className="btn btn-danger"
								onClick={this.handleDeleteTodo.bind(this, theIndex)}
							>
								Borrar
							</button>
						</div>
					</div>
				</div>
			)
		})

		return (
			/************* TODO ACÁ ADENTRO *************/
			<div className="App">

				<Navigation titulo="The Menu" subtitulo="JSX" />

				<div className="container">
					<div className="row">
						<div className="col-md-4">

							<img src={logo} className="App-logo" alt="logo" />

							<TodoForm onAddToDO={this.handleAddToDO} />

						</div>
						<div className="col-md-8">

							<div className="row">
								{itemsToDOs}
							</div>

						</div>
					</div>


				</div>

			</div>
			/************* AFUERA, NADA *************/
		)
	}
}
export default App;
