const HEADERS = new Headers({ "Access-Control-Allow-Origin" : "*" })

export async function getAll(){

	//return await fetch(`${API_URL}/tasks`).then( data => data.json() )
	return await fetch(`${API_URL}/tasks`,{
		method : "GET",
		headers : HEADERS,
	}).then( data => data.json() )

}
